import 'dart:convert';
import 'dart:io';

import 'package:chia_utils/chia_crypto_utils.dart';
import 'package:chia_utils/src/api/chia_full_node_interface.dart';
import 'package:chia_utils/src/api/full_node_http_rpc.dart';
import 'package:chia_utils/src/api/models/responses/chia_base_response.dart';
import 'package:chia_utils/src/core/models/address.dart';
import 'package:chia_utils/src/core/models/models.dart';

// TODO: add wrapper class that holds test logic (everything besides actual simulator api calls)
class SimulatorHttpRpc extends FullNodeHttpRpc {
  const SimulatorHttpRpc(String baseUrl, {String? certPath, String? keyPath}) : super(
    baseUrl, certPath: certPath, keyPath: keyPath,
  );

  static const testAddress = Address('xch1pdar6hnj8c9sgm74r72u40ed8cnpduzan5vr86qkvpftg0v52jkstxap9z');
  static const otherAddress = Address('xch1ye5dzd44kkatnxx2je4s2agpwtqds5lsm5mlyef7plum5danxalq2dnqap');

  Future<ChiaBaseResponse> farmTransactionBlock({Address address = testAddress}) async {
    final responseData = await client.sendRequest(
      Uri.parse('farm_tx_block'),
      {'address': address.address},
    );
    FullNodeHttpRpc.mapResponseToError(responseData);

    return ChiaBaseResponse.fromJson(jsonDecode(responseData.body) as Map<String, dynamic>);
  }

  Future<void> moveToNextBlock() async {
    await farmTransactionBlock(address: otherAddress);
  }

  Future<void> mintCats() async {
    const coinSpendsJson = '[{"coin": {"parent_coin_info": "0xe3b0c44298fc1c149afbf4c8996fb92400000000000000000000000000000001", "puzzle_hash": "0x0b7a3d5e723e0b046fd51f95cabf2d3e2616f05d9d1833e8166052b43d9454ad", "amount": 1750000000000}, "puzzle_reveal": "", "solution": "0xff80ffff01ffff33ffa01c2121e077ca2d57e3a447718fa42c3064bdf13bc7bf07bb3f39d084beb8c23eff82271080ffff33ffa0f794b351f24652734d4081887ef5addee7e84997b4216de591e1c9ff9ac98fb7ff8601977420b4f080ffff3cffa0612f6c1bdf609aa05265c9dc29eabdb7973f8496f26965defbe3240e29ab65668080ff8080"}, {"coin": {"parent_coin_info": "0x26081b15441311d9a207a078b650a05766975814fd5aa6935a759ddaf2a05af0", "puzzle_hash": "0x1c2121e077ca2d57e3a447718fa42c3064bdf13bc7bf07bb3f39d084beb8c23e", "amount": 10000}, "puzzle_reveal": "", "solution": ""}]';
    var l = jsonDecode(coinSpendsJson) as Iterable;
    final spends = List<CoinSpend>.from(l.map<CoinSpend>((dynamic model)=> CoinSpend.fromJson(model as Map<String, dynamic>)));
    final aggSig = JacobianPoint.fromBytesG2([128, 115, 176, 31, 102, 152, 209, 254, 244, 249, 12, 51, 40, 145, 135, 253, 133, 60, 146, 181, 55, 110, 207, 137, 54, 6, 182, 38, 140, 113, 163, 98, 203, 20, 200, 80, 208, 99, 183, 82, 32, 123, 151, 155, 206, 65, 73, 74, 9, 25, 201, 75, 249, 211, 243, 225, 222, 89, 88, 5, 214, 137, 183, 181, 10, 33, 31, 14, 30, 189, 53, 116, 255, 135, 202, 139, 216, 120, 57, 159, 76, 68, 123, 160, 78, 205, 189, 48, 2, 7, 241, 77, 192, 129, 61, 196]);
    final spendBundle = SpendBundle(coinSpends: spends, aggregatedSignature: aggSig);
    await pushTransaction(spendBundle);
  }

  Future<List<Coin>> getCoins() async {
    final fullNodeInterface = ChiaFullNodeInterface(this);
    final coins = await fullNodeInterface.getCoinsByPuzzleHashes([testAddress.toPuzzlehash()]);
    return coins;
  }
}
